<?php 	get_header();	$page_sidebar = $sidebar = '';	$page_sidebar = rd_options('reedwan_page_sidebar'); 	if($page_sidebar == 'sidebar_none'){$sidebar='grid_12';}	else{$sidebar='grid_9';}		$post_loop_count = 1;	$page = (get_query_var('paged')) ? get_query_var('paged') : 1;	if($page > 1) $post_loop_count = ((int) ($page - 1) * (int) get_query_var('posts_per_page')) +1;	?><div class="page-content column_1<?php if($page_sidebar == 'sidebar_left'){echo ' left-sidebar';} ?>">	<div class="row clearfix">		<div class="row_inner">			<div class="pages search-page <?php echo $sidebar; ?>">				<div class="search-again">					<div class="ma"><h4><?php _e('New Search','corporative'); ?></h4></div>					<div class="ma"><p><?php _e('If you are not happy with the results below please do another search','corporative'); ?></p></div>					<div id="search-again-form" class="search-again-form">						<?php get_search_form(); ?>					</div>				</div>			<?php if (have_posts()) : ?> 				<?php while (have_posts()) : the_post(); ?>				<div class="grid_item">					<div class="post_b">						<div class="post_content">						<span class="post_count" title=""><?php echo $post_loop_count; ?></span>							<div class="post_inner_content clearfix">								<h2 class="post_title"> <a href="<?php the_permalink(); ?>" title=""><?php the_title(); ?> </a></h2>								<div class="f_meta s_meta clearfix">									<div><?php the_time('F j, Y'); ?></div>									<div><?php the_author_link(); ?></div>									<?php if (has_category( )): ?>									<div><?php the_category(', '); ?></div>									<?php endif; ?>								</div>								<?php echo excerpt(60); ?>							</div>						</div>					</div>				</div>				<?php $post_loop_count++; ?>				<?php endwhile; wp_reset_postdata(); ?>				<?php kriesi_pagination(); ?>			<?php else: ?>				<div class="no-result">					<div class="ma"><p><?php _e('Your search','corporative');?> - <strong><?php echo get_search_query(); ?></strong> - <?php _e('did not match any documents.','corporative');?></p></div>					<div class="ma"><h5><?php _e('Suggestions:','corporative');?></h5></div>					<div class="list-icon">						<ul>						<li><i class="fa-check"></i><?php _e('Make sure all words are spelled correctly.','corporative');?></li>						<li><i class="fa-check"></i><?php _e('Try different keywords.','corporative');?></li>						<li><i class="fa-check"></i><?php _e('Try more general keywords.','corporative');?></li>						</ul>					</div>				</div>			<?php endif; ?>			</div>			<?php if($page_sidebar != 'sidebar_none'): ?>				<div class="sidebar grid_3">					<?php get_sidebar();?>				</div>			<?php endif; ?>		</div>	</div></div><!-- end page content --><?php get_footer(); ?>